create DATABASE `itdatatech`;
create table itdatatech.APP_USER ( id BIGINT NOT NULL AUTO_INCREMENT, ssoid VARCHAR(30) NOT NULL, password VARCHAR(100) NOT NULL, PRIMARY KEY (id), UNIQUE (ssoid) );
create table itdatatech.USER_PROFILE( id BIGINT NOT NULL AUTO_INCREMENT, type VARCHAR(30) NOT NULL, PRIMARY KEY (id), UNIQUE (type) );
CREATE TABLE itdatatech.APP_USER_USER_PROFILE ( user_id BIGINT NOT NULL, user_profile_id BIGINT NOT NULL, PRIMARY KEY (user_id, user_profile_id), CONSTRAINT FK_APP_USER FOREIGN KEY (user_id) REFERENCES itdatatech.APP_USER (id), CONSTRAINT FK_USER_PROFILE FOREIGN KEY (user_profile_id) REFERENCES itdatatech.USER_PROFILE (id) );
CREATE TABLE itdatatech.PERSISTENT_LOGINS ( username varchar(64) NOT NULL, series varchar(64) NOT NULL, token varchar(64) NOT NULL, last_used timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (series) ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE itdatatech.CONTENT ( id bigint(20) NOT NULL AUTO_INCREMENT, Name varchar(30) NOT NULL, Description varchar(100) NOT NULL, Path varchar(255) NOT NULL, Categories varchar(100) NOT NULL, Created datetime NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY (id) ) ENGINE=InnoDB AUTO_INCREMENT=28 ;
